import React, { useState } from 'react';
import axios from 'axios';

const AddProductForm = ({ eventId, onProductAdded }) => {
    const [newProduct, setNewProduct] = useState({ name: '', price: '', quantity: '' });

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNewProduct({ ...newProduct, [name]: value });
    };

    const handleSubmit = (e) => {
        e.preventDefault(); // Previne o comportamento padrão de recarregar a página
        if (newProduct.name && newProduct.price > 0 && newProduct.quantity > 0) {
            axios.post(`http://localhost:5000/events/${eventId}/products`, newProduct)
                .then(response => {
                    console.log("Produto adicionado:", response.data); // Verifique se o produto está sendo retornado
                    onProductAdded(response.data); // Notifica que um novo produto foi adicionado
                    setNewProduct({ name: '', price: '', quantity: '' }); // Limpa os campos
                })
                .catch(error => console.error('Erro ao adicionar produto:', error));
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <h2>Adicionar Novo Produto</h2>
            <input
                type="text"
                name="name"
                value={newProduct.name}
                onChange={handleInputChange}
                placeholder="Nome do Produto"
                required
            />
            <input
                type="number"
                name="price"
                value={newProduct.price}
                onChange={handleInputChange}
                placeholder="Preço"
                required
                min="0"
            />
            <input
                type="number"
                name="quantity"
                value={newProduct.quantity}
                onChange={handleInputChange}
                placeholder="Quantidade"
                required
                min="1"
            />
            <button type="submit">Adicionar Produto</button>
        </form>
    );
};

export default AddProductForm;
