import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // Importação do useNavigate



function EventForm() {
    const [event, setEvent] = useState({
        name: '',
        date: '',
        location: '',
        participants: []
    });

    const navigate = useNavigate(); // Hook para navegação


    const handleChange = (e) => {
        setEvent({ ...event, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:5000/events', event);
            const createdEventId = response.data._id; // Supondo que o ID do evento criado é retornado na resposta
            console.log('ID do evento criado:', createdEventId);
            alert('Evento criado com sucesso!');
            navigate(`/events/${createdEventId}`); // Redireciona para a página de detalhes do evento
        } catch (error) {
            alert('Erro ao criar evento.');
        }
    };

    return (
        <div className="event-form">
            <h2>Criar Novo Evento</h2>
            <form onSubmit={handleSubmit}>
                <label>
                    Nome do Ba r:
                    <input type="text" name="name" value={event.name} onChange={handleChange} required />
                </label>
                <label>
                    Data do Evento:
                    <input type="date" name="date" value={event.date} onChange={handleChange} required />
                </label>
                <label>
                    Endereço do Bar:
                    <input type="text" name="location" value={event.location} onChange={handleChange} required />
                </label>
                <button type="submit">Criar Evento</button>
            </form>
        </div>
    );
}

export default EventForm;
