import React, { useState } from 'react';
import axios from 'axios';

const AddProductForm = ({ eventId, onProductAdded }) => {
    const [newProduct, setNewProduct] = useState({ name: '', price: '', quantity: '' });

    const handleAddProduct = () => {
        if (newProduct.name && newProduct.price > 0 && newProduct.quantity > 0) {
            axios.post(`http://localhost:5000/events/${eventId}/products`, newProduct)
                .then(response => {
                    onProductAdded(response.data); // Notifica que um novo produto foi adicionado
                    setNewProduct({ name: '', price: '', quantity: '' }); // Limpa os campos
                })
                .catch(error => console.error('Erro ao adicionar produto:', error));
        }
    };

    return (
        <div>
            <h2>Adicionar Novo Produto</h2>
            <input
                type="text"
                value={newProduct.name}
                onChange={(e) => setNewProduct({ ...newProduct, name: e.target.value })}
                placeholder="Nome do Produto"
            />
            <input
                type="number"
                value={newProduct.price}
                onChange={(e) => setNewProduct({ ...newProduct, price: e.target.value })}
                placeholder="PreÃ§o"
            />
            <input
                type="number"
                value={newProduct.quantity}
                onChange={(e) => setNewProduct({ ...newProduct, quantity: e.target.value })}
                placeholder="Quantidade"
            />
            <button onClick={handleAddProduct}>Adicionar Produto</button>
        </div>
    );
};

export default AddProductForm;
