import React, { useState, useEffect } from 'react';

const FechamentoConta = ({ eventId }) => {
    const [participants, setParticipants] = useState([]);
    const [products, setProducts] = useState([]);

    // Fetch dos produtos e participantes
    useEffect(() => {
        const fetchEventData = async () => {
            // Busque produtos e participantes do evento
            const response = await fetch(`/api/events/${eventId}`);
            const data = await response.json();
            setProducts(data.products); // Produtos do evento
            setParticipants(data.participants); // Participantes do evento
        };
        fetchEventData();
    }, [eventId]);

    // Função para lidar com a atribuição de produtos a participantes - Atualiza a quantidade consumida de cada produto para cada participante.
    const handleProductSelection = (participantId, productId, quantity) => {
        const updatedParticipants = participants.map((participant) => {
            if (participant._id === participantId) {
                const consumptionIndex = participant.consumptions.findIndex(
                    (consumption) => consumption.productId === productId
                );

                if (consumptionIndex > -1) {
                    participant.consumptions[consumptionIndex].quantity = quantity;
                } else {
                    participant.consumptions.push({ productId, quantity });
                }
            }
            return participant;
        });
        setParticipants(updatedParticipants);
    };

    // Renderização da página
    return (
        <div>
            <h2>Fechamento da Conta</h2>
            <div className="participants-list">
                {participants.map((participant) => (
                    <div key={participant._id}>
                        <h3>{participant.name}</h3>
                        <div className="product-selection">
                            {products.map((product) => (
                                <div key={product._id}>
                                    <span>{product.name} - R${product.price}</span>
                                    <input
                                        type="number"
                                        placeholder="Quantidade"
                                        onChange={(e) =>
                                            handleProductSelection(
                                                participant._id,
                                                product._id,
                                                parseInt(e.target.value, 10)
                                            )
                                        }
                                    />
                                </div>
                            ))}
                        </div>
                    </div>
                ))}
            </div>
            <button onClick={() => saveConsumptions(participants)}>Salvar e Calcular</button>
        </div>
    );
};

export default FechamentoConta;
